import React, { useState, useEffect } from 'react'
import { View, Text, ActivityIndicator, FlatList } from 'react-native'
import { useNavigation } from '@react-navigation/core'
import firebase from '../firebase'
import { ListItem } from 'react-native-elements'

export default function RestaurantScreen(props) {
  const navigation = useNavigation()
  const { restauranteId } = props.route.params

  const [restaurante, setRestaurante] = useState({});
  const [loading, setLoading] = useState(true)

  const [orden, setOrden] = useState([]);
  const [menu, setMenu] = useState([]);

  const getRestaurantById = async (id) => {
    const ref = firebase.db.collection('restaurantes').doc(id);
    const doc = await (await ref.get()).data()
    setRestaurante(doc)
    navigation.setOptions({
      title: doc.nombre, headerStyle: {
        backgroundColor: doc.color,
      },
    })

    setLoading(false)
    console.log(doc.menu)
    console.log(doc)
  }

  const maperarMenu = () => {
    restaurante.menu.forEach(element => {
      setOrden(...orden, { item, cantidad: 0 })
    });
    console.log(orden)
  }

  useEffect(async () => {
    getRestaurantById(restauranteId);
    setTimeout(() => { }, 500)
    maperarMenu();
  }, [])

  if (loading) {
    return <View style={{ flex: 1, alignItems: 'center', alignContent: 'center' }}><ActivityIndicator /></View>
  }

  const keyExtractor = (item, index) => index.toString()

  const renderItem = ({ item }) => (
    <ListItem bottomDivider>
      <ListItem.Chevron />
      <ListItem.Content>
        <ListItem.Title>{item.platillo}</ListItem.Title>
        <ListItem.Subtitle>${item.precio}</ListItem.Subtitle>
      </ListItem.Content>
    </ListItem>
  )

  return (
    <View>
      <Text>This is the page for the {restaurante.menu[0].precio} Restaurant</Text>
      {
        restaurante.menu.map((item, i) => (
          <ListItem key={i} bottomDivider>
            <ListItem.Chevron />
            <ListItem.Content>
              <ListItem.Title>{item.platillo}</ListItem.Title>
              <ListItem.Subtitle>${item.precio}</ListItem.Subtitle>
            </ListItem.Content>
            <Text>Hola</Text>
          </ListItem>
        ))
      }

      {/*<FlatList keyExtractor={keyExtractor} data={restaurante.menu} renderItem={renderItem} />*/}
    </View>
  )
}
