import { useNavigation } from '@react-navigation/core';
import React, { useState, useEffect } from 'react'
import { KeyboardAvoidingView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native'
import auth from '../firebase';
const LoginScreen = () => {

    const navigation= useNavigation()

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(user => { 
            if (user) 
            navigation.navigate('Home') 
        })
        return unsubscribe;
    }, [])


    const handleSignUp = () => {
        auth.createUserWithEmailAndPassword(email, password)
            .then(userCredentials => {
                const user = userCredentials.user
                console.log(user.email)
            }).catch(error => console.log(error))
    }

    const handleLogin = () => {
        auth.signInWithEmailAndPassword(email, password)
            .then(userCredentials => {
                const user = userCredentials.user
                console.log('logged with email')
            }).catch(error => alert(error.code))
    }

    return (
        <KeyboardAvoidingView style={styles.container} behavior='height'>
            <View style={styles.inputconntainer}>
                <TextInput placeholder='email' value={email} onChangeText={text => setEmail(text.replaceAll('\s+',''))} style={styles.input} />
                <TextInput placeholder='password' value={password} onChangeText={text => setPassword(text)} style={styles.input} secureTextEntry />
            </View>
            <View style={styles.buttoncontainer}>
                <TouchableOpacity onPress={handleLogin } style={styles.button}>
                    <Text style={styles.buttonText}>Iniciar sesión</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={handleSignUp} style={[styles.button, styles.buttonOutLine]}>
                    <Text style={styles.buttonOutLineText}>Registrarse</Text>
                </TouchableOpacity>
            </View>
        </KeyboardAvoidingView>
    )
}

export default LoginScreen

const styles = StyleSheet.create({
    inputconntainer: {
        width: '80%',
    },
    container: {
        justifyContent: 'center',
        alignItems: 'center',
        flex: 1,
        minWidth: 400,
        maxWidth: 1000,
        marginHorizontal: 'auto'
    },
    input: {
        backgroundColor: 'white',
        paddingHorizontal: 15,
        paddingVertical: 10,
        borderRadius: 10,
        marginTop: 5,
        borderColor: 'lightgray',
        borderWidth: 1
    },
    buttoncontainer: {
        width: '60%',
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 40
    },
    button: {
        width: '100%',
        backgroundColor: '#0782f9',
        padding: 15,
        borderRadius: 10,
        alignItems: 'center'
    },
    buttonText: {
        color: 'white',
        fontWeight: 'bold',
    },
    buttonOutLine: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782f9',
        borderWidth: 2
    },
    buttonOutLineText: { color: '#0782f9', fontWeight: 'bold' },

})
