import { useNavigation } from '@react-navigation/core'
import React from 'react'
import { Alert, Platform, StyleSheet, Text, TouchableOpacity, View, Scrollview, ScrollView, TextInput, Button } from 'react-native'
import { BackgroundImage } from 'react-native-elements/dist/config'
import auth from '../firebase'
import { Icon } from 'react-native-elements'

const HomeScreen = () => {

    const navigation = useNavigation()

    const handleSignOut = () => {
        if (Platform.OS == 'ios' | Platform.OS == 'android')
            Alert.alert('Cerrar Sesión', '¿Está seguro de cerrar sesión?', [{ text: 'Aceptar', onPress: signOutProcess }, { text: 'Cancelar', onPress: () => { console.log('calcelando...') } }])
        else {
            signOutProcess();
        }
    }

    const signOutProcess = () => {
        auth.signOut()
            .then(() => {
                navigation.replace("Login")
            })
            .catch((error) => {
                alert(error.message)
            })
    }



    return (
        <ScrollView>
            <View style={{ flex: 1, flexDirection: 'row', justifyContent: 'space-between', padding: 5, backgroundColor: '#FE2746', alignItems: 'center' }}>
                <View style={{ flex: 1, justifyContent: 'center', textAlign: 'center', }}><Text style={{ color: 'white' }}>Bienvenido</Text><Text style={{ color: 'white' }}>{auth.currentUser?.email}</Text></View>
                <TouchableOpacity onPress={handleSignOut} style={styles.button}>
                    <Text style={styles.buttonText}>Cerrar sesión</Text>
                </TouchableOpacity>
            </View>
            <View style={{ width: '100%', height: '30vh' }}>
                <BackgroundImage source={{ uri: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8Zm9vZHxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60' }} style={{ width: '100%', height: '100%' }}>
                    <View style={{ width: '100%', height: '100%', backgroundColor: 'rgba(24, 50, 89, 0.5)' }}>
                        <Text style={{ position: 'relative', left: '5%', top: '40%', color: 'white', fontWeight: 'bold', fontSize: '2rem' }}>¿Qué se te antoja ordenar hoy?</Text>
                        <View style={{ flex: 1, flexDirection: 'row', maxWidth: 800, minWidth: 400, marginHorizontal: 'auto', backgroundColor: 'white', borderRadius: 30, alignItems: 'center', padding: 5,position:'relative',top:'50%' }}>
                            <Icon style={{ flex: 1 }} name='magnifying-glass' type='entypo' />
                            <TextInput style={{ flex: 11, padding: 5 }} placeholder='Comida china' />
                        </View>
                    </View>
                </BackgroundImage>

            </View>
        </ScrollView>
    )
}

export default HomeScreen

const styles = StyleSheet.create({
    container: {

        flex: 1,
        minWidth: 300,
        maxWidth: 1000,
        marginHorizontal: 'auto'
    },
    button: {
        //width: '100%',
        marginTop: 5,
        backgroundColor: '#FE2746',
        padding: 15,
        borderColor: 'white',
        borderWidth: 3,
        borderRadius: 10,
        alignItems: 'center'
    },
    buttonText: {
        color: 'white',
        fontWeight: 'bold',
    },
    textHighlight: {
        color: '#FE2746'
    },

})
