import { useNavigation } from '@react-navigation/core'
import React from 'react'
import { Alert, Platform, StyleSheet, Text, TouchableOpacity, View, Scrollview, ScrollView } from 'react-native'
import { BackgroundImage } from 'react-native-elements/dist/config'
import auth from '../firebase'

const HomeScreen = () => {

    const navigation = useNavigation()

    const handleSignOut = () => {
        if(Platform.OS=='ios'|Platform.OS=='android')
        Alert.alert('Cerrar Sesión','¿Está seguro de cerrar sesión?',[{text:'Aceptar',onPress: signOutProcess},{text:'Cancelar',onPress:()=>{console.log('calcelando...')}}])
        else{
            signOutProcess();
        }
    }

    const signOutProcess = () => {
        auth.signOut()
       .then(()=>{
        navigation.replace("Login")})
       .catch((error)=>{
       alert(error.message)})
    }

    return (
        <ScrollView>
            <View style={{flex:1,flexDirection:'row', justifyContent:'space-between',padding:5,backgroundColor:'#FE2746', alignItems:'center'}}>
           <View style={{flex:1,justifyContent:'center',textAlign:'center',}}><Text style={{ color: 'white'}}>Bienvenido</Text><Text style={{ color: 'white'}}>{auth.currentUser?.email}</Text></View>
            <TouchableOpacity onPress={handleSignOut} style={styles.button}>
                <Text style={styles.buttonText}>Cerrar sesión</Text>
            </TouchableOpacity>
            </View>
            <View style={{width:'100%', height:'60vh'}}>
                <BackgroundImage source={{uri:'https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8Zm9vZHxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60'}} style={{width: '100%', height:'90%'}}>
                <View style={{width:'100%', height:'80%', backgroundColor:'rgba(255, 255, 255, 0.51)'}}>
                    <Text>¿Qué se te antoja ordenar hoy?</Text>
                </View>
                </BackgroundImage>
            </View>
        </ScrollView>
    )
}

export default HomeScreen

const styles = StyleSheet.create({
    container: {
        
        flex: 1,
        minWidth: 300,
        maxWidth: 1000,
        marginHorizontal: 'auto'
    },
    button: {
        //width: '100%',
        marginTop: 5,
        backgroundColor: '#FE2746',
        padding: 15,
        borderColor: 'white',
        borderWidth:3,
        borderRadius: 10,
        alignItems: 'center'
    },
    buttonText: {
        color: 'white',
        fontWeight: 'bold',
    },
    textHighlight:{
        color:'#FE2746'
    },
    
})
